import React, { useState } from 'react';
import './Form.css';

function Form() {
  // Define state for each form field
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [city, setCity] = useState('');

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate the form inputs
    if (!name || !email || !phone || !city) {
      alert('Please fill out all fields');
      return;
    }

    try {
      // Send a POST request to the backend with the form data
      const response = await fetch('', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Name: name, Phone_no: phone, email, city }),
      });

      const data = await response.json();

      if (response.ok) {
        alert('Your inquiry has been submitted successfully.');
        // Clear the form
        setName('');
        setEmail('');
        setPhone('');
        setCity('');
      } else {
        alert(data.message || 'An error occurred while submitting your inquiry.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting your inquiry.');
    }
  };

  return (
    <div className="quote-form">
      <h2>Get Free Quote</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Your Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="tel"
          placeholder="Your Phone No"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Your City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        />
        <button type="submit">Send Message</button>
      </form>
    </div>
  );
}

export default Form;
