import React, { useState } from 'react';
import axios from 'axios';
import './Form.css';

function Form() {
  // Define state for each form field
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [city, setCity] = useState('');
  const [loading, setLoading] = useState(false);  // For loading state

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate the form inputs
    if (!name || !email || !phone || !city) {
      alert('Please fill out all fields');
      return;
    }

    setLoading(true);  // Set loading state to true while submitting

    try {
      // Send a POST request to the backend with the form data
      const response = await axios.post('/api/user-enquiry', {
        Name: name,
        Phone_no: phone,
        email,
        city,
      });

      if (response.status === 200) {
        alert('Your inquiry has been submitted successfully.');
        // Clear the form
        setName('');
        setEmail('');
        setPhone('');
        setCity('');
      } else {
        alert(response.data.message || 'An error occurred while submitting your inquiry.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting your inquiry.');
    } finally {
      setLoading(false);  // Set loading state back to false after submission
    }
  };

  return (
    <div className="quote-form">
      <h2>Get Free Quote</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Your Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="tel"
          placeholder="Your Phone No"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Your City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Sending...' : 'Send Message'}
        </button>
      </form>
    </div>
  );
}

export default Form;
