import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const PackageDetails = () => {
  const { id } = useParams();
  const [itinerary, setItinerary] = useState([]);
  const [packageName, setPackageName] = useState("");

  useEffect(() => {
    // Fetch itinerary details based on package ID
    fetch(`http://localhost:7000/api/v1/Iternaries/get/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setItinerary(data); // Assuming data is an array of day-wise itineraries
        if (data.length > 0) setPackageName(data[0].name); // Set package name
      })
      .catch((error) => console.error("Error fetching itinerary:", error));
  }, [id]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>{packageName} Itinerary</h2>
      {itinerary.length === 0 ? (
        <p>Loading itinerary...</p>
      ) : (
        itinerary.map((day, index) => (
          <div
            key={index}
            style={{
              marginBottom: "20px",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "8px",
            }}
          >
            <h3>Day {day.day_no}</h3>
            <p><strong>Highlights:</strong> {day.highlights}</p>
            <h4>Activities:</h4>
            <ul>
              {day.activities.map((activity, idx) => (
                <li key={idx}>
                  <strong>{activity.activityName}</strong>: {activity.description} <br />
                  <em>Time:</em> {activity.time} <br />
                  <em>Location:</em> {activity.location}
                </li>
              ))}
            </ul>
            <h4>Locations:</h4>
            <ul>
              {day.locations.map((location, idx) => (
                <li key={idx}>
                  <strong>{location.name}</strong>: {location.description} <br />
                  <em>Address:</em> {location.address}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default PackageDetails;
